$(document).ready(function(){$(".reservation-remove-btn").on("click",function(e){return!!confirm("Usunąć wybraną rezerwację?")}),$("#reservations-tbl .reservation-row").on({mouseenter:function(e){$(".day-cell[data-reservation-id="+$(this).data("reservation-id")+"]").addClass("reservation-active")},mouseleave:function(e){$(".day-cell[data-reservation-id="+$(this).data("reservation-id")+"]").removeClass("reservation-active")}})});